import { ChangeDetectionStrategy, Component } from '@angular/core';

@Component({
  selector: 'app-traffic-lights-screen',
  templateUrl: './traffic-lights-screen.component.html',
  styleUrls: ['./traffic-lights-screen.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class TrafficLightsScreenComponent {
  protected isSynchronized = false;

  protected toggleSynchronization() {
    this.isSynchronized = !this.isSynchronized;
  }

  trackByIndex(index: number): number {
    return index;
  }
}
<div class="toolbar">
  <button (click)="toggleSynchronization()">
    {{ isSynchronized ? "Chaos" : "Synchronize" }}
  </button>
</div>

<cdk-virtual-scroll-viewport itemSize="100" class="traffic-lights-container">
  <div class="traffic-lights">
    <app-traffic-light
      *cdkVirtualFor="let light of [].constructor(1000); trackBy: trackByIndex"
      [isSynchronized]="isSynchronized"
    ></app-traffic-light>
  </div>
</cdk-virtual-scroll-viewport>
