import {Component, OnInit} from '@angular/core';
import {TrafficLightService} from "../../services/traffic-light.service";

@Component({
  selector: 'app-traffic-lights-screen',
  templateUrl: './traffic-lights-screen.component.html',
  styleUrls: ['./traffic-lights-screen.component.scss'],
})
export class TrafficLightsScreenComponent implements OnInit {

   isSynchronized = false;

  constructor(private trafficLightService: TrafficLightService) {
  }

  ngOnInit(): void {
    this.trafficLightService.startCycle(this.isSynchronized)
  }

  protected toggleSynchronization() {
    this.isSynchronized = !this.isSynchronized;
    this.trafficLightService.synchronizationStatus = this.isSynchronized;
  }

  trackByIndex(index: number): number {
    return index;
  }
}
<div class="toolbar">
  <button (click)="toggleSynchronization()">
    {{ isSynchronized ? 'Chaos' : 'Synchronize' }}
  </button>
</div>

<div class="traffic-lights">
  <app-traffic-light
    *ngFor="let light of [].constructor(1000); trackBy: trackByIndex"
  ></app-traffic-light>
</div>
