import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, Subject, timer } from 'rxjs';
import { switchMap, takeUntil } from 'rxjs/operators';
import { Light } from '../model/lights-data';

@Injectable({
  providedIn: 'root',
})
export class TrafficLightService {
  private readonly colors: Light[][] = [
    ['red'],
    ['red', 'yellow'],
    ['green'],
    ['yellow'],
  ];
  private readonly durations = [3000, 1500, 3000, 1500];
  private colorIndex = 0;

  private state$ = new BehaviorSubject<Light[]>(['red']);
  private stop$ = new Subject<void>();

  get currentState$(): Observable<Light[]> {
    return this.state$.asObservable();
  }

  startCycle(isSynchronized: boolean): void {
    const randomDelay = isSynchronized ? 0 : Math.random() * 5000;

    this.stopCycle();

    timer(randomDelay, this.durations[this.colorIndex])
      .pipe(
        takeUntil(this.stop$),
        switchMap(() => {
          this.colorIndex = (this.colorIndex + 1) % this.colors.length;
          this.state$.next(this.colors[this.colorIndex]);
          return timer(this.durations[this.colorIndex]).pipe(
            takeUntil(this.stop$),
          );
        }),
      )
      .subscribe();
  }

  set synchronizationStatus(value: boolean) {
    this.stopCycle();
    this.state$.next(['red']);
    this.startCycle(value);
  }

  stopCycle(): void {
    this.stop$.next();
  }
}
